<?php/* 	Appointment: Управление кешем	File: cache.php */if(!defined('MOZG'))	die('Hacking attempt!');echoheader();echohtmlstart('Чистка кеша пользователей');// Чистим кеш профиля определенного пользователяif(isset($POST['cid'])){$user_id = intval($_POST['cuid']);mozg_clear_cache_file('user_'.$user_id.'/profile_'.$user_id);}// Чистим кеш по списку ID - ОТ и ДОif(isset($POST['ciduids'])){$start_id =intval($_POST['startuid']);$end_id =intval($_POST['enduid']);while($start_id < $end_id){$start_id++;$user_id = $start_id;mozg_clear_cache_file('user_'.$user_id.'/profile_'.$user_id);}}// Чистим вообще весь кешif(isset($POST['cidall'])){    $files = glob("application/cache/*");    $c = count($files);    if (count($files) > 0) {        foreach ($files as $file) {                  if (file_exists($file)) {            unlink($file);            }           }    }}function dirsize($directory){	if(!is_dir($directory)) return - 1;	$size = 0;	if($DIR = opendir($directory)){		while(($dirfile = readdir($DIR)) !== false){			if(@is_link($directory.'/'.$dirfile) || $dirfile == '.' || $dirfile == '..') continue;			if(@is_file($directory.'/'.$dirfile)) $size += filesize($directory . '/' . $dirfile);			else if(@is_dir($directory.'/'.$dirfile)){				$dirSize = dirsize($directory.'/'.$dirfile);				if($dirSize >= 0) $size += $dirSize;				else return - 1;			}		}		closedir( $DIR );	}	return $size;}$cache_size = formatsize(dirsize("application/cache"));echo'<div class="fllogall">Размер папки application/cache/:</div> <div style="margin-bottom:10px">'.$cache_size.'&nbsp;</div><div class="mgcler"></div><form method="POST">Удалить кеш конкретного ID: <input type="text" name="cuid"><input type="submit" name="cid" value="Удалить!"></form><br><br><form method="POST">Удалить кеш нескольких ID: От ID:<input type="text" name="startuid"> До ID:<input type="text" name="enduid"><input type="submit" name="ciduids" value="Удалить!"></form><br><br><form method="POST">Удалить весь кеш, хуй знает зачем: <input type="submit" name="cidall" value="Удалить весь кеш!"></form>';echohtmlend();?>